wwv_flow_imp_page.create_report_region(
 p_id=>wwv_flow_imp.id(7403996339456089395)
,p_name=>'Territories Cards'
,p_region_name=>'cards_region'
,p_template=>wwv_flow_imp.id(7352355885510316168)
,p_display_sequence=>20
,p_include_in_reg_disp_sel_yn=>'Y'
,p_region_template_options=>'#DEFAULT#'
,p_component_template_options=>'#DEFAULT#:u-colors:t-Cards--basic:t-Cards--3cols:t-Cards--animColorFill'
,p_region_attributes=>'style="display:none"'
,p_source_type=>'NATIVE_SQL_REPORT'
,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with base_territories as (',
'  select id,',
'    territory_name,',
'    territory_description,',
'    row_key,',
'    updated,',
'    (',
'      select count(*)',
'      from eba_sales_customers c',
'      where t.id = c.customer_territory_id',
'    ) accounts,',
'    (',
'      select count(*)',
'      from eba_sales_deals',
'      where customer_id in (',
'        select id ',
'        from eba_sales_customers ',
'        where customer_territory_id = t.id',
'      )',
'    ) opportunities,',
'    (',
'      select count(*)',
'      from eba_sales_deals',
'      where deal_probability != 0',
'        and deal_probability != 100',
'        and customer_id in (',
'          select id ',
'          from eba_sales_customers ',
'          where customer_territory_id = t.id',
'        )',
'    ) open_opportunities',
'  from eba_sales_territories t',
'  where :P50_DISPLAY_AS = ''CARDS''',
'),',
'card_details as (',
'  select territory_name card_title,',
'    null card_icon,',
'    row_key card_initials,',
'    case ',
'      when length(territory_description) < 50',
'      then territory_description',
'      else substr(territory_description, 1, 50) || ''...''',
'    end card_text,',
'    apex_util.prepare_url(',
'      ''f?p='' ||',
'      :APP_ID ||',
'      '':93:'' ||',
'      :APP_SESSION ||',
'      '':::93:P93_ID:'' ||',
'      id',
'    ) card_link,',
'    to_char(accounts,''999G999G999G990'') ||',
'      '' accounts, '' ||',
'      to_char(opportunities,''999G999G999G990'') ||',
'      '' opportunities'' ||',
'      case open_opportunities',
'        when 0 then null',
'        else '', '' || to_char(open_opportunities,''999G999G999G990'') || '' open''',
'      end card_stats,',
'    updated card_subtext,',
'    accounts,',
'    open_opportunities,',
'    opportunities,',
'    territory_description',
'  from base_territories',
')',
'select *',
'from card_details',
'where (',
'  :P50_SEARCH is null',
'    or instr(upper(card_title), upper(:P50_SEARCH)) > 0',
'    or instr(upper(card_initials), upper(:P50_SEARCH)) > 0',
'    or instr(upper(territory_description), upper(:P50_SEARCH)) > 0',
'    or instr(upper(card_stats), upper(:P50_SEARCH)) > 0',
'    or instr(upper(''Updated: '' || apex_util.get_since(card_subtext)), upper(:P50_SEARCH)) > 0',
'  )',
'order by',
'  case :P50_SORT',
'    when ''NAME'' then card_title',
'  end,',
'  case :P50_SORT',
'    when ''ACC'' then accounts',
'  end desc,',
'  case :P50_SORT',
'    when ''OPENOPP'' then open_opportunities',
'  end desc,',
'  case :P50_SORT',
'    when ''OPP'' then opportunities',
'  end desc,',
'  case :P50_SORT',
'    when ''UPDATED'' then card_subtext',
'  end desc'))
,p_ajax_enabled=>'Y'
,p_ajax_items_to_submit=>'P50_SEARCH,P50_DISPLAY_AS,P50_SORT'
,p_lazy_loading=>false
,p_query_row_template=>wwv_flow_imp.id(7409384263994580659)
