'        and (',
'          instr(upper(l.lead_name_01), upper(:P116_SEARCH)) > 0',
'            or instr(upper(l.row_key), upper(:P116_SEARCH)) > 0',
'            or instr(upper(l.lead_details), upper(:P116_SEARCH)) > 0',
'            or instr(upper(l.tags), upper(:P116_SEARCH)) > 0',
